mapper.select:
select wad.id, sc1.`name` as audit_status, wa.work_name, wa.work_content, wa.work_instructor, wa.work_minutes, wa.origin_work_id, wad.audit_time as time, wad.audit_fail_reason, sc2.`name` as workFrom, wa.origin_work_id from work_audit_detail as wad left join work_audit as wa on wad.audit_item_id=wa.id left join sys_config as sc1 on wad.audit_status=sc1.code left join sys_config as sc2 on wa.work_from=sc2.code
wa.work_user_id=#{userId}
and work_name like "%"#{workName}"%"
and wad.audit_status like #{auditStatus}
and wa.work_from like #{workFrom}
order by wa.audit_submit_time desc limit #{pageStart},#{pageSize}

select count(*)
from work_audit_detail as wad
left join work_audit as wa
on wad.audit_item_id=wa.id
left join sys_config as sc1
on wad.audit_status=sc1.code

    wa.work_user_id=#{userId}

        and work_name like "%"#{workName}"%"


        and wad.audit_status like #{auditStatus}


        and wa.work_from like #{workFrom}

mapper.update:
update work_pool
set work_name=

    when #{item.id} then #{item.workName}
,
work_content=

    when #{item.id} then #{item.workContent}
,
work_instructor=

    when #{item.id} then #{item.workInstructor}
,
work_minutes=

    when #{item.id} then #{item.workMinutes}
,
work_from=

    when #{item.id} then #{item.workFrom}
,
update_time = NOW(),
audit_status='agree'
where id in
(

    #{item.id}

)

mapper.select:
select wp.*, users.real_name as employeeName from work_pool wp left join usermanagement.`users` on wp.user_id=`users`.id
work_from='w3'
and users.id=#{employeeId}
and wp.work_name like "%"#{workName}"%"
and wp.delete_status='no' and wp.audit_status='agree'
order by update_time desc,create_time desc limit #{pageStart},#{pageSize}

select * from work_pool where id in
#{item.id}
and delete_status='no' order by update_time desc,create_time desc

mapper.delete:
update work_pool
set delete_status='yes'
where id=#{workId}

mapper.update:
update work_pool
set audit_status=

    when #{item.id} then #{item.auditStatus}

where id in

    #{item.id}
