mapper.select:
SELECT wa.id, wa.work_name, wa.work_content, users.real_name AS submitter, wa.work_instructor, wa.work_minutes, sc.`name` AS workFrom, wa.audit_submit_time, wad.audit_status, sc1.name as workPriority FROM work_audit_detail AS wad LEFT JOIN work_audit AS wa ON wad.audit_item_id = wa.id LEFT JOIN usermanagement.`users` ON wa.work_user_id = `users`.id LEFT JOIN sys_config AS sc ON wa.work_from = sc. CODE left join sys_config as sc1 on wa.work_priority=sc1.code
wad.audit_status = 'unaudited'
and locate(#{workName},wa.work_name)
and users.id=#{employeeId}
and users.department_id=#{departmentId}
order by wa.audit_submit_time desc limit #{pageStart},#{pageSize}

select count(*) from ( select wa.id FROM work_audit_detail AS wad LEFT JOIN work_audit AS wa ON wad.audit_item_id = wa.id LEFT JOIN usermanagement.`users` ON wa.work_user_id = `users`.id LEFT JOIN sys_config AS sc ON wa.work_from = sc. CODE
wad.audit_status = 'unaudited'
and locate(#{workName},wa.work_name)
and users.id=#{employeeId}
and users.department_id=#{departmentId}
) a

SELECT wa.id, wa.work_name, wa.work_content, users.real_name AS submitter, wa.work_instructor, wa.work_minutes, sc.`name` AS workFrom, wa.audit_submit_time, wad.audit_status, sc1.name as workPriority FROM work_audit_detail AS wad LEFT JOIN work_audit AS wa ON wad.audit_item_id = wa.id LEFT JOIN usermanagement.`users` ON wa.work_user_id = `users`.id LEFT JOIN sys_config AS sc ON wa.work_from = sc. CODE left join sys_config as sc1 on sc1.code=wa.work_priority
wad.audit_status not like 'unaudited'
and locate(#{workName},wa.work_name)
and users.id=#{employeeId}
and users.department_id=#{departmentId}
and audit_user_id=#{auditUserId}
order by wad.audit_time desc limit #{pageStart},#{pageSize}

SELECT count(*) FROM work_audit_detail AS wad LEFT JOIN work_audit AS wa ON wad.audit_item_id = wa.id LEFT JOIN usermanagement.`users` ON wa.work_user_id = `users`.id LEFT JOIN sys_config AS sc ON wa.work_from = sc. CODE
wad.audit_status not like 'unaudited'
and locate(#{workName},wa.work_name)
and users.id=#{employeeId}
and users.department_id=#{departmentId}
and audit_user_id=#{auditUserId}

mapper.update:
update work_audit_detail
set
audit_user_id=
case audit_item_id

    when #{item.auditItemId} then #{item.auditUserId}

end,
audit_time=
case audit_item_id

    when #{item.auditItemId} then NOW()

end,
audit_fail_reason=
case audit_item_id

    when #{item.auditItemId} then #{item.auditFailReason}

end,
audit_status=
case audit_item_id

    when #{item.auditItemId} then #{item.auditStatus}

end
where audit_item_id in

    #{item.auditItemId}

mapper.select:
select *
from work_audit_detail as wad
where wad.id=#{id}

mapper.update:
update work_audit_detail
set audit_status='cancel'
where  id=#{id}

update work_audit set origin_work_id= case id
when #{item.id} then #{item.originWorkId}
end where id in
#{item.id}
