mapper.select:
select wad.id, sc1.`name` as audit_status, wa.work_name, wa.work_content, wa.work_instructor, wa.work_minutes, wa.origin_work_id, wad.audit_time as time, wad.audit_fail_reason, sc2.`name` as workFrom from work_audit_detail as wad left join work_audit as wa on wad.audit_item_id=wa.id left join sys_config as sc1 on wad.audit_status=sc1.code left join sys_config as sc2 on wa.work_from=sc2.code
wa.work_user_id=#{userId}
and work_name like "%"#{workName}"%"
and wad.audit_status like #{auditStatus}
and wa.work_from like #{workFrom}
order by time desc limit #{pageStart},#{pageSize}

select count(*)
from work_audit_detail as wad
left join work_audit as wa
on wad.audit_item_id=wa.id
left join sys_config as sc1
on wad.audit_status=sc1.code

    wa.work_user_id=#{userId}
    
        and work_name like "%"#{workName}"%"
    
    
        and wad.audit_status like #{auditStatus}
    
    
        and wa.work_from like #{workFrom}

select count(*) from work_pool as wp
wp.user_id=#{employeeId}
and wp.work_name like "%"#{workName}"%"

mapper.update:
update work_pool
set work_name=

    when #{item.id} then #{item.workName}
,
work_content=

    when #{item.id} then #{item.workContent}
,
work_instructor=

    when #{item.id} then #{item.workInstructor}
,
work_minutes=

    when #{item.id} then #{item.workMinutes}
,
work_from=

    when #{item.id} then #{item.workFrom}
,
update_time = NOW()
where id in
(

    #{item.id}

)

mapper.select:
select wp.*, employee.emp_name as employeeName from work_pool wp left join `user` on wp.user_id=`user`.id left join employee on `user`.employee_id=employee.id
work_from='w3'
and employee.id=#{employeeId}
and wp.work_name="%"#{workName}"%"
order by update_time desc limit #{pageStart},#{pageSize}
