mapper.select:
select * from(select wad.id, sc1.`name` as audit_status, wa.work_name, wa.work_content, wa.work_instructor, wa.work_minutes, wa.origin_work_id, wad.audit_time as time from work_audit_detail as wad left join work_audit as wa on wad.audit_item_id=wa.id left join sys_config as sc1 on wad.audit_status=sc1.code where wad.audit_time is not null UNION select wad.id, sc1.`name` as audit_status, wa.work_name, wa.work_content, wa.work_instructor, wa.work_minutes, wa.origin_work_id, wa.audit_submit_time as time from work_audit_detail as wad left join work_audit as wa on wad.audit_item_id=wa.id left join sys_config as sc1 on wad.audit_status=sc1.code where wad.audit_time is null)a
work_name like "%"#{workName}"%"
order by time desc limit #{pageStart},#{pageSize}

select wp.id, wp.work_name, wp.work_content, wp.work_instructor, wp.work_minutes, `user`.username from work_pool as wp LEFT JOIN `user` on `user`.id=wp.user_id
wp.work_from='w3'
and wp.user_id=#{employeeId}
and wp.work_name like "%"#{workName}"%"
limit #{pageStart},#{pageSize}

select count(*) from work_pool as wp
wp.user_id=#{employeeId}
and wp.work_name like "%"#{workName}"%"
