mapper.select:
select wa.id, wa.work_name, wa.work_content, employee.emp_name as submitter, wa.work_instructor, wa.work_minutes, sc.`name` as workFrom, wa.audit_submit_time from work_audit_detail as wad left join work_audit as wa on wad.audit_item_id=wa.id left join `user` on wa.work_user_id=`user`.id left join employee on employee.id=`user`.employee_id left join sys_config as sc on wa.work_from=sc.code
emp_job not like '经理' and wad.audit_status='unaudited'
and dept_number like #{deptNumber}
and wa.work_name like "%"#{workName}"%"
and employee_id=#{employeeId}
order by wa.audit_submit_time desc limit #{pageStart},#{pageSize}

select count(*) from ( select wa.id from work_audit_detail as wad left join work_audit as wa on wad.audit_item_id=wa.id left join `user` on wa.work_user_id=`user`.id left join employee on employee.id=`user`.employee_id left join sys_config as sc on wa.work_from=sc.code
emp_job not like '经理' and wad.audit_status='unaudited'
and dept_number like #{deptNumber}
and wa.work_name like "%"#{workName}"%"
and employee_id=#{employeeId}
) a

mapper.update:
update work_audit_detail
set
audit_user_id=
case audit_item_id

    when #{item.auditItemId} then #{item.auditUserId}

end,
audit_time=
case audit_item_id

    when #{item.auditItemId} then NOW()

end,
audit_fail_reason=
case audit_item_id

    when #{item.auditItemId} then #{item.auditFailReason}

end,
audit_status=
case audit_item_id

    when #{item.auditItemId} then #{item.auditStatus}

end
where audit_item_id in

    #{item.auditItemId}
