mapper.select:
select wsd.id, wp.work_name, wp.work_content, wp.work_instructor, wp.work_minutes, sc1.name as work_from, sc.name as finish_status, wsd.finish_picture, wsd.finish_feedback, wsd.finish_condition, wsd.finish_time from work_schedule_detail as wsd left join sys_config as sc1 on wsd.work_from=sc1.`code` left join work_pool as wp on wsd.work_id=wp.id left join sys_config as sc on wsd.finish_status=sc.`code`
user_id=#{userId} and schedule_id=#{scheduleId}
and wsd.work_from=#{workFrom}
and work_name like "%"#{workName}"%"
and wsd.finish_status like #{finishStatus}
order by wsd.finish_time asc

mapper.update:
update work_schedule_detail
set finish_status=

    when #{item.id} then #{item.finishStatus}
,
finish_picture=

    when #{item.id} then #{item.finishPicture}
,
finish_condition=

    when #{item.id} then #{item.finishCondition}
,
finish_feedback=

    when #{item.id} then #{item.finishFeedback}
,
finish_time=

    when #{item.id} then #{item.finishTime}

where id in

    #{item.id}

mapper.select:
select id
from work_schedule as ws
where date_format(ws.date,'%e %b %y') = date_format(now(), '%e %b %y') and ws.user_id=#{userId}

select * from work_schedule
user_id=#{employeeId}
and date_format(`date`,'%e %b %y') = date_format(#{date}, '%e %b %y')

select wsd.id, wp.work_name, wp.work_content, wp.work_instructor, wp.work_minutes, sc1.name as work_from, sc.name as finish_status, wsd.finish_picture, wsd.finish_feedback, wsd.finish_condition, wsd.finish_time from work_schedule_detail as wsd left join sys_config as sc1 on wsd.work_from=sc1.`code` left join work_pool as wp on wsd.work_id=wp.id left join sys_config as sc on wsd.finish_status=sc.`code`
user_id=#{employeeId}
and schedule_id=#{scheduleId}
order by wsd.finish_time asc limit #{pageStart},#{pageSize}

select count(*) from work_schedule_detail as wsd left join sys_config as sc1 on wsd.work_from=sc1.`code` left join work_pool as wp on wsd.work_id=wp.id left join sys_config as sc on wsd.finish_status=sc.`code`
user_id=#{employeeId}
and schedule_id=#{scheduleId}
