mapper.select:
select *
from work_schedule
where user_id=#{userId} and `date`=#{date}

select wsd.id, wp.work_name, wp.work_content, wp.work_instructor, wp.work_minutes, sc1.name as work_from, wsd.finish_status, wsd.finish_picture, wsd.finish_condition from work_schedule_detail as wsd left join sys_config as sc1 on wsd.work_from=sc1.`code` left join work_pool as wp on wsd.work_id=wp.id
user_id=#{userId} and schedule_id=#{scheduleId}
and wsd.work_from=#{workFrom}
and work_name like "%"#{workName}"%"
